/**
 * Log levels for use with {@link Logger}
 */
export declare enum LogLevel {
    /** If set on a logger, ignore all log messages */
    IGNORE = -1,
    /** Error messages */
    ERROR = 0,
    /** Warning messages */
    WARN = 1,
    /** Informational messages */
    INFO = 2,
    /** Debugging messages */
    DEBUG = 3,
    /** Verbose debugging messages */
    TRACE = 4
}
